class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        vector<int> a(n + k - 1);
        for(int i = 0; i < a.size(); i++)
        {
            a[i] = colors[i % n];
        }

        int cnt = 0, ans = 0;
        for(int i = 0; i < a.size(); i++)
        {
            cnt++;
            if(cnt >= k)
            {
                ans++;
            }
            if((i + 1) < a.size() && a[i] == a[i + 1])
            {
                cnt = 0;
            }
        }
        return ans;
    }
};
