class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0, n=colors.size();
        vector<int> a(n,0);
        for(int i=0; i<n; i++){
            int prev, next;
            if(i==n-1){
                prev=n-2;
                next=0;
            }
            else if(i==0){
                prev = n-1;
                next=1;
            }
            else{
                prev = i-1;
                next=i+1;
            }
            if(colors[i]!=colors[prev] && colors[i]!=colors[next]) a[i]=1;
        }

       int c=0,left=0, right=0, s=0;
        for(int i=0; i<k; i++) s+=a[right++];
        right--;

        if(k == 2 + s-a[right]-a[left]) ans++;

        for(int i=0; i<n-1; i++){
            s -= a[left];
            if(left==n-1) left=0;
            else left++;

            if(right==n-1) right=0;
            else right++;

            s+=a[right];

            if(k == 2 + s-a[right]-a[left]) ans++;
        }

        return ans;
    }
};
