class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k > n) return 0;

        colors.insert(colors.end(), colors.begin(), colors.begin() + k - 1);

        int ans = 0;
        int transitions = 0;


        for (int i = 1; i < k; ++i) {
            if (colors[i] != colors[i - 1]) {
                transitions++;
            }
        }


        for (int i = k - 1; i < n + k - 1; ++i) {

            if (transitions == k - 1) {
                ans++;
            }


            if (i < n + k - 2) {
                if (colors[i - k + 2] != colors[i - k + 1]) {
                    transitions--;
                }
                if (colors[i + 1] != colors[i]) {
                    transitions++;
                }
            }
        }

        return ans;
    }
};
