class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        for (int i = 0; i < k; i++)
            c.push_back(c[i]);
        int count = 0;
        int tempK = k;
        int ind = 1;
        while (ind < n + k - 1) {
            int prev = ind - 1;
            int curr = ind;
            if (c[prev] != c[curr])
                tempK--;
            else
                tempK = k;
            if (tempK <= 1)
                count++;
            ind++;
        }

        return count;
    }
};
