class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> c=colors;
        // for(auto i:colors){
        //     c.push_back(i);
        // }
        for(int i=0;i<k-1;i++){
            c.push_back(colors[i]);
        }
        int i=0,j=1;
        int n=c.size(),cnt=0;
        while(j<n && j-i<=k){
            if(j-i==k){
                cnt++;
                i++;
                }
            if(c[j]==c[j-1]){
                i=j;
            }

                j++;

        }
         if(j-i==k){
                cnt++;
         }
        return cnt;
    }
};
