class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<k-1;i++)
        {
            colors.push_back(colors[i]);
        }
        int w=colors.size();
        int ans=0;
        int i=0,j=1;
        while(j<w)
        {
            if((j-i)==k)
            {
                ans++;
                i++;
            }
            if(colors[j]==colors[j-1])
            {   
                i=j;
            }

            j++;
        }
        if((j-i)==k)ans++;
        return ans;
    }
};
