    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n = colors.size(); 
        int count = 0; 
        if (k == 1) { 
            return n; 
        } 
      // solve(n);
        // for(int i=0;i<n;i++){
        //     cout<<colors[i]<<"";
        // }
        // for(int i=0;i<n;i++){
        //     cout<<colors[i]<<"";
        // }
        // solve(n);
        int a = 1; 
        int i = 1; 
        while (i < n + k - 1) { 
            int c = i; 
            int p = i - 1; 
            if (c >= n) c -= n;
            if (p >= n)  p -= n; 
            if (colors[c] != colors[p]) { 
                ++a; 
            } else { 
                a = 1; 
            } 

            if (a >= k) { 
                ++count; 
            } 

            ++i; 
        } 
        // solve(n);
        // for(int i=0;i<n;i++){
        //     cout<<colors[i]<<"";
        // }

        return count; 
    } 
};
