class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {



     for(int i=0;i<k-1;i++){
v.push_back(v[i]);
     }

int n=v.size();
int s=0;
int ans=0;

for(int i=0;i<v.size()-1;i++){
    if(v[i]==v[i+1]){
     int l=i-s+1;
        if(l>=k)ans+=l-k+1;
        s=i+1;
    }


}
int x=-1;
for(int i=n-1;i>1;i--){
    if(v[i]==v[i-1]){x=i;break;}
}

if(x==-1){
    if(n-k>=0)
    return n-k+1;
    return 0;
}


else{
    int l=n-x;
    if(l>=k){
        return ans+ l-k+1;
    }
return ans;

}
return ans;

    }
};
