class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size(),fs=0,cnt=1,val=a[0];
        for(int i=0;i<n;i++)a.push_back(a[i]);
        for(int i=1;i<2*n;i++)
        {
            if(i-k>=n-1)break;
            if(a[i]!=a[i-1])
            {
                cnt++;
                if(cnt>=k)fs++;
            }
            else cnt=1;

        }
        return fs;
    }
};
