class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
         int cnt = 1;
        for(int i=0;i<k-1;i++){
            col.push_back(col[i]);
        }
        vector<int>ans;
        for(int i=1;i<col.size();i++){
            if(col[i] == col[i-1]){
                ans.push_back(cnt);
                cnt = 1;
            }
            else ++cnt;
        }
        ans.push_back(cnt);
        int val = 0;
        for(auto it:ans)
            if(it>=k)val += it-k+1;

        return val;
    }
};
