class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int> nums = c;
        int m = c.size();
        for(auto &x : c) nums.push_back(x);
        int n = nums.size();
        int curr = 0;
        int v = c[0];

        int j = 0;  int ans = 0;

        for(int i=0; i<n; ++i){


            if(j >= m) break;


            if(curr == k){
                ans++;
                curr--;
                j++;
            }
            if(nums[i] == v){
                v = !v;
                curr++;
            }else{
                curr = 1;
                v = !nums[i];
                j = i;
            }
            // cout<<curr<<endl;
        }
        return ans;


    }
};
