class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
       int l = c.size();
       int ct = 1;
       vector<int>v;
       for(int i = 0;i < l-1;i++){
            if(c[i] != c[i+1]) ct++;
            else{
                v.push_back(ct);
                ct = 1;
            }
       }
        v.push_back(ct);
        if(v.size() == 1 && c[0] != c[l-1]){
            return c.size();
        }
        if(c[0] != c[l-1]) {
            v[0] += v[v.size()-1];
            v.pop_back();
        }
        int ans = 0;
        for(auto x:v){
            if(x >= k){
                ans += x-k+1;
            }
        }
        return ans;
    }
};
