class Solution:
    def numberOfAlternatingGroups(self, l: List[int], k: int) -> int:
        if len(l)<k:return 0
        if k==1:return len(l)
        p=i=1
        c=0
        while i<len(l)+k-1:
            x,y=i,i-1 
            if x>=len(l):
                x-=len(l)
            if y>=len(l):y-=len(l)
            if l[x]!=l[y]:
                p+=1
            else:
                p=1
            if p>=k:c+=1
            i+=1
        return c
