class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        a = 1
        n = len(colors)
        r = 0
        for i in range(1, n + k - 1):
            if i == 1:
                if colors[1] != colors[0]:
                    a = min(a + 1, k)
                else:
                    a = 1
            elif i == n:
                if colors[0] != colors[n-1]:
                    a = min(a + 1, k)
                else:
                    a = 1
            elif i == n+1:
                if colors[1] != colors[0]:
                    a = min(a + 1, k)
                else:
                    a = 1
            else:
                if colors[i % n] != colors[(i-1) % n]:
                    a = min(a + 1, k)
                else:
                    a = 1
            if a == k:
                r += 1
        return r
