class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for (int i = 0; i < k - 1; ++i) {
            a.push_back(a[i]);
        }
        int i = 0, res = 0;
        for (int j = 1; j <= a.size(); ++j) {
            if (j == a.size() || a[j] == a[j-1]) {
                res += max(j-i-k+1, 0);
                i = j;
            }
        }
        return res;
    }
};
