class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int cnt=0, i=0, j=1;
        while (i<v.size() && j<v.size()+k){
            int idx=j, end=idx%v.size();  
            while(v[end]!=v[(end-1+v.size())%v.size()] && idx<v.size()+k-1)
                idx++, end=idx%v.size();
            cnt+=max(idx-i-k+1, 0);
            i=idx; j=i+1;
        } return cnt;
    }
};
