class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
      int n=colors.size(),l=0,r=1,cnt=0;
        while(r<n+k&&l<n){
            int val=r;
            int val2=val%n;
            while(colors[val2]!=colors[(val2-1+n)%n]&&val<n+k-1){
                val++;
                val2=val%n;
            }
            cnt+=max(val-l-k+1,0);
            l=val;
            r=l+1;

        }
        return cnt;
    }
};
