class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        vector<int> st(n);
        int cnt=2;
        int lst=a[n-1];
        st[n-1]=1;
        for(int i=n-2;i>=0;i--)
        {
            if(a[i]!=lst)st[i]=cnt++;
            else
            {
                cnt=1;
                st[i]=cnt++;
            }
            lst=a[i];
        }
        cnt=0;
        for(int i=0;i<n;i++)
        {
            if(i+k>n)
            {
                if(st[i]==n-i and st[0]>=k-(n-i) and a[0]!=a[n-1]){
                    cnt++;
                }
            }
            else
            {
                if(st[i]>=k)cnt++;
            }
        }
        return cnt;
    }
};
