class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cols, int k) {
        int n = cols.size();
        int cnt = 0;

        if (k == 1) {
            return n;
        }

        int altLen = 1;
        int idx = 1;
        while (idx < n + k - 1) {
            int cur = idx;
            int prev = idx - 1;

            if (cur >= n) {
                cur -= n;
            }
            if (prev >= n) {
                prev -= n;
            }

            if (cols[cur] != cols[prev]) {
                ++altLen;
            } else {
                altLen = 1;
            }

            if (altLen >= k) {
                ++cnt;
            }

            ++idx;
        }

        return cnt;
    }
};
