class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        dp = [1] * len(colors)
        for i in range(1, len(colors)):
            if colors[i-1] == colors[i]:
                dp[i] = 1
            else:
                dp[i] = min(dp[i-1] + 1, k)
        for i in range(0, k):
            if colors[i-1] == colors[i]:
                dp[i] = 1
            else:
                dp[i] = min(dp[i-1] + 1, k)
        return sum(v >= k for v in dp)
