class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& hues, int m) {
        deque<int> sequence;
        int start = 0;
        int end = 0;
        int result = 0;
        int length = hues.size();

        for(int idx = 0; idx < min(length, m - 1); idx++) {
            hues.push_back(hues[idx]);
        }

        length = hues.size();

        while(end < length) {
            if(sequence.empty()) {
                sequence.push_back(hues[end]);
                end++;    
            } else {
                if(sequence.back() == hues[end]) {
                    sequence.clear();
                    sequence.push_back(hues[end]);
                } else {
                    sequence.push_back(hues[end]);

                    if(sequence.size() >= m) {
                        result++;
                    }
                }
                end++;
            }
        }
        return result;
    }
};
