class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int good = 0;
        int n = c.size();
        for(int i= 1;i<k ; i++){
            if(c[i]!=c[i-1]){
                good++;
            }
        }
        for(int i= 0 ; i< k-1 ; i++){
            c.push_back(c[i]);
        }
        int ans =0;
        if(good == k-1)ans++;
        cout<<good<<" ";
        for(int i = 1; i< n ; i++){
            if(c[i]!=c[i-1])good--;
            if(c[i+k-1]!=c[i+k-2])good++;
            if(good == k-1)ans++;
            cout<<good<<" ";
        }
        int j = 0;
        return ans;
    }
};
