class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& petals, int bouquet) {
        vector<int> groups;
        int lastP = petals[0];
        int cGroup = 1;
        bool s = false;

        for (int i = 1; i < petals.size(); i++) {
            if (petals[i] == lastP) {
                groups.push_back(cGroup);
                cGroup = 1;
                s = true;
            } else {
                cGroup++;
            }
            lastP = petals[i];
        }
        groups.push_back(cGroup);

        if (groups.size() == 1 && groups[0] == petals.size() && petals[0] != petals[petals.size() - 1] && !s) {
            return petals.size();
        }

        if (petals[0] != petals[petals.size() - 1] && groups.size() > 1) {
            int lastG = groups.back();
            groups[0] += lastG;
            groups.pop_back();
        }

        long long r = 0;
        for (int g : groups) {
            r += max(0, g - (bouquet - 1));
        }

        return (int) r;
    }

