class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        if(k==1) return n;
        int j=1;
        int len=1;
        int cnt=0;
        while(j<n+k-1){
            int temp=j;
            int prev=j-1;
            if(temp>=n) temp=temp-n;
            if(prev>=n) prev=prev-n;
            if(colors[temp]!=colors[prev])len++;
            else len=1;
            if(len>=k) cnt++;
            j++;
        }
        return cnt;
    }
};
