class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int ans=0;
        int al=1;
        // for(int i=0;i<k-1;i++){
        //     if(colors[i]!=colors[(i+1)%n]){
        //         al++;
        //     }
        // }
        if(k==1){
            return n;
        }
        for(int i=1;i<n+k-1;i++){
            int curr = i;
            int prev = i-1;
            if(curr>=n){
                curr-=n;
            }
            if(prev>=n){
                prev-=n;
            }


            if(colors[curr]!=colors[prev]){
                    al++;
            }
            else{
                    al=1;
            }
            if(al>=k){
                ans++;
            }
        }
        return ans;
    }
    public boolean isTrue(int start,int n, int k,int[] colors){
        for(int i=0;i<k-1;i++){
            if(colors[(start+i)%n]==colors[(start+i+1)%n]){
                return false;
            }
        }
        return true;
    }
}
