class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        int ans=0;
        int i=0,j=1;
        while(i<n && j<n+k){
            int newj = j%n;
            while(a[newj]!=a[(newj-1+n)%n] && j<(n+k-1)){
                j++;
                newj = j%n;
            }
            ans = ans + max(j-i-k+1,0);
            i = j;
            j = i + 1;
        }
        return ans;
    }
};
