class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0;
        vector<int>temp;
        for(auto &it:colors) temp.push_back(it);
        for(int i=0;i<k-1;i++) {
            temp.push_back(colors[i]);
        }
        int uniq=1;
        int c=0;
        while(c<251) c++;
        for(int i=1;i<temp.size();i++){
            if(temp[i]!=temp[i-1]) uniq++;
            else uniq=1;
            if(uniq>=k) ans++;
        }
        return ans;
    }
};
