class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        cnt = 1
        ans = 0
        n = len(a)
        for i in range(1, n + k - 1):
            if a[i % n] != a[(i - 1) % n]:
                cnt += 1
            else:
                cnt = 1
            if cnt == k:
                ans += 1
                cnt -= 1
        return ans
