class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0;i<n;i++)
        {
            colors.push_back(colors[i]);
        }

        int i = 0;
        int j = 1;
        int ans = 0;
        while(i<n and i!=(j%n))
        {
            if(colors[j]==colors[j-1])
            {
              i = j;
            }
            if((j-i+1)==k)
            {
                ans++;
                i++;
            }   
            j++;
        }
        return ans;
    }
};
