class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int flag = !colors[0];
        int cnt = 1;
        int ans = 0;
        for(int i=1;i<(n+k-1);i++){
            int val = colors[i%n];
            if(val == flag){
                cnt++;
                flag  = !flag;
                if(cnt >= k) ans++;
            }
            else{
                flag = !val;
                cnt = 1;
            }
        }

        return ans;
    }
};
