class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        N = len(colors)
        colors = colors + colors

        for i in range(N * 2):
            if i % 2 == 0:
                colors[i] = 1 - colors[i]

        #print(colors)
        count = 0
        streak = 1
        for i in range(N + k - 2):
            if colors[i] == colors[i + 1]:
                streak += 1
            else:
                streak = 1

            if streak >= k:
                count += 1
        return count
