class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        int prev = 1-v[0], cnt = 0;
        for (int i = 0; i < k-1; i++) {
            if(v[i]!=prev) cnt++;
            else cnt = 1;
            prev = v[i];
            v.push_back(v[i]);
        }
        int ans = 0;
        for(int i = k-1; i < v.size(); i++) {
            if(v[i]!=prev) cnt++;
            else cnt = 1;
            prev = v[i];
            if(cnt>=k) ans++;
        }
        return ans;
    }
};
