class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ok[200005];
        fill(ok,ok+colors.size()*2,0);
        for(int i = 0;i<colors.size();i++){
            if(colors[i]!=colors[(i+1)%colors.size()]){
                ok[i]=1;
                ok[i+colors.size()]=1;
            }
        }
        int cnt=0;
        for(int i = 0;i<k-1;i++){
            cnt+=ok[i];
        }
      //  for(int i = 0;i<2*colors.size();i++)printf("%d ",ok[i]);
     //   printf("\n");
        int ans=0;
        for(int i = k-1;i<colors.size()+k-1;i++){
            if(cnt==k-1)ans++;
          //  if(k==colors.size())break;
            cnt-=ok[i-(k-1)];
            cnt+=ok[i];
        }
        return ans;
    }
};
