class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        vector<int> b;
        for(int i=0;i<2*n;i++)b.push_back(c[i%n]);
        int cu=0;
        int col[2*n];
        col[0]=cu;
        for(int i=1;i<2*n;i++)
        {
            if(b[i%n] != b[(i-1)%n])cu++;
            col[i]=cu;
        }
        int ans=0;
        for(int i=0;i<n;i++)
        {
            if(col[i]+k-1 == col[i+k-1])ans++;
        }
        return ans;
    }
};
