class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),cnt=0,ans=0;
        colors.insert(colors.end(),colors.begin(), colors.end());
        for(int i=0,j=0;i<n;i++){
            while(j-i+1<k){
                if(colors[j+1]==colors[j]) cnt++;
                j++;
            }
            if(cnt==0) ans++;
            if(colors[i+1]==colors[i]) cnt--;
        }
        return ans;
    }
};
