class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for(int i = 0; i < n; i++) a.push_back(a[i]);
        vector<int> dp(2 * n);
        for(int i = 1; i < 2 * n; i++){
            if(a[i] == a[i - 1]) continue;
            else if(dp[i - 1] == 0) dp[i - 1] = 1;
            dp[i] = dp[i - 1] + 1;
        }
        int ans = 0;
        for(int i = 0; i < n; i++){
            if(a[i] != a[i + 1] && dp[i] + k - 1 == dp[i + k - 1]) ans += 1;
        }
        return ans;
    }
};
