class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        for(int i=0;i<k-1;i++){
            a.push_back(a[i]);
        }
        vector<int> b(n+k-1);
        b[0]=1;
        for(int i=1;i<n+k-1;i++){
            if(a[i]==a[i-1]) b[i]=0;
            else b[i]=1;
        }
        for(int i=1;i<n+k-1;i++){
            b[i]+=b[i-1];
        }
        // for(auto it:a) cout<<it<<" ";
        // cout<<endl;
        // for(auto it:b) cout<<it<<" ";
        // cout<<endl;
        // return 0;
        int ans=0;
        for(int i=0;i<n;i++){
            if(b[i+k-1]-b[i]==k-1) ans++;
        }
        return ans;
    }
};
