class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0 ; i < k-1 ; i++){
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        int ans = 0;
        vector<int> store;
        for(int i = 0 ; i < n-1 ; i++){
            if(colors[i] != colors[i+1]){
                store.push_back(1);
            }
            else store.push_back(0);
        }
        n = store.size();
        int sum = 0;
        for(int i = 0 ; i < k-1 ; i++){
            sum += store[i];
        }
        if(sum == k-1) ans++;
        for(int i = 1 ; i < n ; i++){
            int end = i+k-1-1;
            if(end == n) break;
            sum -= store[i-1];
            sum += store[end];
            if(sum == k-1) ans++;
        }
        return ans;
    }
};
