class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for (int i = 0; i < k; ++i) colors.push_back(colors[i]);

        int ret = 0;
        for (int i = 0, j = 0; i < n; ++i) {
            if (j < i) j = i;
            while (j + 1 < n + k && colors[j] != colors[j + 1]) ++j;
            if (j - i + 1 >= k) ++ret;
        }
        return ret;
    }
};
