class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        mem = [None for _ in colors]

        ends = []
        for i, (x, y) in enumerate(zip(colors, colors[1:] + [colors[0]])):
            if x == y:
                ends.append(i)

        # print(ends)
        if not ends:
            return len(colors)
        ans = 0
        for e0, e1 in zip(ends, ends[1:] + [ends[0]]):
            if e1 <= e0:
                e1 += len(colors)
            diff = e1 - e0
            ans += max(diff - k + 1, 0)
        return ans
