class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        t = colors[0]
        ans = int(k == 1)
        s = 1
        for i in colors[1:] + colors[:k-1]:
            if t ^ i == 1:
                s += 1
            else:
                s = 1
            t = i
            ans += s >= k
        return ans
