class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n=colors.length;

        int res=0;
        int r=0;
        int x=0;
        --k;
        for (int i = 0; i < n; ) {
            while(x<k){
                int nx=(r+1)%n;
                if(colors[nx]!=colors[r]){
                    r=nx;
                    ++x;
                }else{
                    if(r<i){
                        return res;
                    }
                    i=r+1;
                    r++;
                    x=0;
                    break;
                }
            }

            if(x==k) {
                ++i;
                ++res;
                --x;
            }
        }

        return res;
    }
}
