class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        int orig = n;
        for (int i=0;i<n;i++)
            a.push_back(a[i]);
        n = a.size();
        vector<int> v;
        for (int i=0;i<n-1;i++)
            v.push_back(a[i] != a[i+1]);
        int m = v.size();
        int pre[m];pre[0]=v[0];
        for (int i=1;i<m;i++)
            pre[i] = v[i] + pre[i-1];
        int ans = 0;
        --k;
        for (int i=0;i<orig;i++)
        {
            // i, i+k-1
            int sum = pre[i+k-1];
            if (i)
                sum -= pre[i-1];
            if (sum == k)
                ans++;
        }
        return ans;
    }
};
