class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int len = 0;
        for(int i = 0; i < n; i++) {
            if(len == 0 || colors[i] != colors[i - 1])
                len++;
            else 
                len = 1;

            if(len == k) {
                ans++;
                len--;
            }
        } 

        for(int i = 0; i < k - 1; i++) {
            if(colors[i] == colors[(i - 1 + n) % n])
                break;
            len++;
            if(len == k) {
                ans++;
                len--;
            }
        }
        return ans;
    }
};
