class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int alter = 0, res = 0;
        for (int i = 0; i < colors.length; ++i) {
            if (i == 0 || colors[i] == colors[i - 1]) {
                alter = 0;
            }
            ++alter;
            if (alter >= k) {
                ++res;
            }
        }
        for (int i = 0; i < k - 1; ++i) {
            if (colors[i] == colors[(i - 1 + colors.length) % colors.length]) {
                alter = 0;
            }
            ++alter;
            if (alter >= k) {
                ++res;
            }
        }
        return res;
    }
}
