class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n=(int)arr.size();
        vector<int> pre(n,0);
        for(int i=0;i<n;i++){
            int l=(i-1+n)%n;
            int r=(i+1)%n;
            if(arr[l]==arr[r] and arr[l]!=arr[i]){
                pre[i]=1;
            }
            if(i){
                pre[i]+=pre[i-1];
            }
        }
        int cnt=0;
        for(int i=0;i<n;i++){
            int j=(i+k-1);
            if(j-1<n){
                int temp=pre[j-1]-pre[i];
                if(temp==k-2) cnt++;
            }
            else{
                int rem=k-(n-i);
                int ind=rem-2;
                int temp=pre[n-1]-pre[i]+pre[ind];
                if(temp==k-2) cnt++;
            }
        }
        return cnt;
    }
};
