class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        l = len(colors)
        colors = colors + colors
        n = len(colors)

        ans = cur = 0
        for i in range(1, n):
            if colors[i] != colors[i - 1]:
                cur += 1
            else:
                cur = 1

            if cur >= k and i >= l:
                ans += 1

        return ans
