class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v = colors;
        for(int i = 0;i<k-1;i++) v.push_back(colors[i]);
        int ans = 0;
        set<int> index;
        for(int i = 1;i<v.size();i++){
            int start = i - k + 1; 
            if(v[i] == v[i-1]){
                index.insert(i - 1);
            }
            if(start >= 0){
                auto it = index.lower_bound(start);
                if(it == index.end()) ans++;
                // else cout << start << " " << i << endl;
            }
        }
        return ans;
    }
};
