class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i=0;i<k;i++) {
            v.push_back(v[i]);
        }
        int n=v.size(),ans=0;
        vector<int> pre(n+1,0);
        for(int i=1;i<n;i++) {
            pre[i]=v[i]!=v[i-1];
            pre[i]+=pre[i-1];
        }
        // for(auto x:pre)  cout<<x<<" "; cout<<endl;
        for(int i=k;i<n;i++) 
            if(pre[i]-pre[i-k+1]==k-1) 
                ans++;

        return ans;
    }
};
