class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int>dc(2*n);
        for(int i=0;i<n;i++){
            dc[i]=dc[i+n]=colors[i];
        }
        int i=0;
        int res=0;
        int fc=dc[0];
        while(i<n){
            int j=i+1;
            while(j<n+k-1 && dc[j]!=fc){
                j++;
                fc=1-fc;
            }
            if(j-i>=k){

                res+=(j-i-k+1);
            }
            i=j;
        }
        return res;
    }
};
